#include<bits/stdc++.h>
#define ll long long int 
using namespace std;

void solve()
{
    ll n;
    cin >> n;

    ll a[n],b[n];
    for(ll i=0; i<n; i++) {
        cin >> a[i];
        b[i] = a[i];
    }

    ll cnt = 0;
    sort(b,b+n);
    for(ll i=0; i<n; i++) {
        if(a[i] != b[i]) cnt++;
    }

    if( cnt == 0) cout << "0" << endl;

    else if(a[0] == 1 || a[n-1] == n) {
        cout << "1" << endl;
    }

    else if(a[0] == n && a[n-1] == 1) {
        cout << "3" << endl;
    }

    else cout << "2" << endl;
}

int main(){
    ios_base::sync_with_stdio(false);

    ll test;
    cin >> test;

    while(test--)
        solve();

    return 0 ;
}
